import React, { useRef, useState, useEffect } from 'react';

export default function CameraCapture({ onCapture }) {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);
  const [cameraOn, setCameraOn] = useState(false);

  const startCamera = async () => {
    if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
      alert('Camera not supported by this browser.');
      return;
    }
    const constraints = { video: { facingMode: 'environment', width: 640, height: 480 } };
    try {
      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      streamRef.current = stream;
      videoRef.current.srcObject = stream;
      videoRef.current.onloadedmetadata = () => videoRef.current.play();
      setCameraOn(true);
    } catch (err) {
      console.error('Camera error:', err.name, err.message);
      alert(
        `Camera error: ${
          err.name === 'NotAllowedError' ? 'Permission denied.' : err.message
        }`
      );
    }
  };

  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      videoRef.current.srcObject = null;
      streamRef.current = null;
      setCameraOn(false);
    }
  };

  const capturePhoto = () => {
    const canvas = canvasRef.current;
    const video = videoRef.current;
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    canvas.getContext('2d').drawImage(video, 0, 0, canvas.width, canvas.height);
    const dataUrl = canvas.toDataURL('image/jpeg', 0.8);
    onCapture(dataUrl);
  };

  useEffect(() => {
    return () => stopCamera(); // Cleanup on unmount
  }, []);

  return (
    <div>
      {!cameraOn && <button onClick={startCamera}>Start Camera</button>}
      <video
        ref={videoRef}
        autoPlay
        playsInline
        muted
        style={{ display: cameraOn ? 'block' : 'none', maxWidth: '100%', height: 'auto' }}
      />
      <canvas ref={canvasRef} style={{ display: 'none' }} />
      {cameraOn && (
        <div>
          <button onClick={capturePhoto}>Capture Photo</button>
          <button onClick={stopCamera}>Stop Camera</button>
        </div>
      )}
    </div>
  );
}
