import React from 'react';

export default function TextEditor({ text, onChange }) {
  const createDocument = (content, filename) => {
    const html = `
      <html xmlns:o='urn:schemas-microsoft-com:office:office' 
            xmlns:w='urn:schemas-microsoft-com:office:word' 
            xmlns='http://www.w3.org/TR/REC-html40'>
        <head><meta charset='utf-8'></head>
        <body>
          <p style="padding:8px;">${content.replace(/\n/g, '<br>')}</p>
        </body>
      </html>
    `;

    const blob = new Blob(['\ufeff', html], { type: 'application/msword' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = filename || 'document.doc';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const handleSave = () => {
    if (!text.trim()) {
      alert('No text to save');
      return;
    }
    createDocument(text, 'text_content.doc');
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
      <textarea
        style={{ width: '100%', height: '200px', fontSize: '1rem' }}
        value={text}
        onChange={(e) => onChange(e.target.value)}
      />
      <button 
        onClick={handleSave}
        style={{
          padding: '8px 16px',
          backgroundColor: '#1e90ff',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer',
          alignSelf: 'flex-end'
        }}
      >
        Save as .doc
      </button>
    </div>
  );
}
