import React, { useState } from 'react';
import CameraCapture from './components/CameraCapture';
import ImageUploader from './components/ImageUploader';
import TextEditor from './components/TextEditor';
import { recognizeText } from './utils/ocr';

function App() {
  const [imageData, setImageData] = useState(null);
  const [extractedText, setExtractedText] = useState('');
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('camera'); // 'camera', 'upload', or 'editor'

  const handleImageInput = async (dataUrl) => {
    setImageData(dataUrl);
    setLoading(true);
    try {
      const text = await recognizeText(dataUrl);
      setExtractedText(text);
      setActiveTab('editor'); // Switch to editor after extraction
    } catch (err) {
      alert('OCR failed: ' + err.message);
    }
    setLoading(false);
  };

  return (
    <div className="app-container" style={{ maxWidth: '800px', margin: '0 auto', padding: '20px' }}>
      <h1 style={{ textAlign: 'center', marginBottom: '20px' }}>Document Processing App</h1>

      <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '20px' }}>
        <button 
          onClick={() => setActiveTab('camera')} 
          style={{ 
            padding: '10px 20px', 
            margin: '0 5px', 
            backgroundColor: activeTab === 'camera' ? '#1e90ff' : '#ddd',
            color: activeTab === 'camera' ? 'white' : 'black',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          Camera
        </button>
        <button 
          onClick={() => setActiveTab('upload')} 
          style={{ 
            padding: '10px 20px', 
            margin: '0 5px', 
            backgroundColor: activeTab === 'upload' ? '#1e90ff' : '#ddd',
            color: activeTab === 'upload' ? 'white' : 'black',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          Upload Image
        </button>
        <button 
          onClick={() => setActiveTab('editor')} 
          style={{ 
            padding: '10px 20px', 
            margin: '0 5px', 
            backgroundColor: activeTab === 'editor' ? '#1e90ff' : '#ddd',
            color: activeTab === 'editor' ? 'white' : 'black',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            display: extractedText ? 'block' : 'none'
          }}
          disabled={!extractedText}
        >
          Text Editor
        </button>
      </div>

      {loading && (
        <div style={{ textAlign: 'center', margin: '20px 0' }}>
          <p>Processing image...</p>
        </div>
      )}

      {activeTab === 'camera' && (
        <CameraCapture onCapture={handleImageInput} />
      )}

      {activeTab === 'upload' && (
        <ImageUploader onUpload={handleImageInput} />
      )}

      {activeTab === 'editor' && extractedText && (
        <div style={{ marginTop: '20px' }}>
          <TextEditor 
            text={extractedText} 
            onChange={setExtractedText} 
          />
          {imageData && (
            <div style={{ marginTop: '20px', textAlign: 'center' }}>
              <h3>Original Image</h3>
              <img 
                src={imageData} 
                alt="Captured" 
                style={{ maxWidth: '100%', maxHeight: '300px', border: '1px solid #ddd' }} 
              />
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
