import React, { useState } from 'react';
import CameraCapture from './components/CameraCapture';
import ImageUploader from './components/ImageUploader';
import TextEditor from './components/TextEditor';
import { recognizeText } from './utils/ocr';

function App() {
  const [imageData, setImageData] = useState(null);
  const [extractedText, setExtractedText] = useState('');
  const [loading, setLoading] = useState(false);
  const [showCamera, setShowCamera] = useState(false); // ðŸ‘ˆ To toggle camera UI

  const handleImageInput = async (dataUrl) => {
    setImageData(dataUrl);
    setLoading(true);
    try {
      const text = await recognizeText(dataUrl);
      setExtractedText(text);
    } catch (err) {
      alert('OCR failed: ' + err.message);
    }
    setLoading(false);
  };

  return (
    <div style={{ padding: '1rem', fontFamily: 'Arial' }}>
      <h1>Image / Camera OCR App</h1>

      <div style={{ marginBottom: '1rem' }}>
        <button onClick={() => setShowCamera(!showCamera)}>
          {showCamera ? 'Close Camera' : 'Use Camera'}
        </button>
        <ImageUploader onUpload={handleImageInput} />
      </div>

      {showCamera && (
        <CameraCapture
          onCapture={(dataUrl) => {
            handleImageInput(dataUrl);
            setShowCamera(false); // close camera after capture
          }}
        />
      )}

      {loading && <p>Processing image, please wait...</p>}

      {imageData && (
        <div style={{ marginBottom: '1rem' }}>
          <h3>Captured/Uploaded Image:</h3>
          <img src={imageData} alt="Captured" style={{ maxWidth: '100%', height: 'auto' }} />
        </div>
      )}

      <TextEditor text={extractedText} onChange={setExtractedText} />
    </div>
  );
}

export default App;
